#!/bin/bash
# Count messages sent to Telegram channel since yesterday

# Get yesterday's date in UTC
YESTERDAY=$(date -u -v-1d +%Y-%m-%d 2>/dev/null || date -u -d "1 day ago" +%Y-%m-%d 2>/dev/null || date -u +%Y-%m-%d)

echo "Counting messages since $YESTERDAY..."
echo ""

# Count by message type
ALPHA_TOUCHED=$(pm2 logs alpha-snipes-paper --lines 50000 --nostream | grep -E "$YESTERDAY|$(date -u +%Y-%m-%d)" | grep -c "Alpha touched" || echo "0")
BOUGHT=$(pm2 logs alpha-snipes-paper --lines 50000 --nostream | grep -E "$YESTERDAY|$(date -u +%Y-%m-%d)" | grep -cE "Bought|Watchlist auto-buy" || echo "0")
SKIPPED=$(pm2 logs alpha-snipes-paper --lines 50000 --nostream | grep -E "$YESTERDAY|$(date -u +%Y-%m-%d)" | grep -c "Skipping" || echo "0")
MILESTONES=$(pm2 logs alpha-snipes-paper --lines 50000 --nostream | grep -E "$YESTERDAY|$(date -u +%Y-%m-%d)" | grep -c "Milestone" || echo "0")
EXITS=$(pm2 logs alpha-snipes-paper --lines 50000 --nostream | grep -E "$YESTERDAY|$(date -u +%Y-%m-%d)" | grep -cE "Early TP|Trailing stop|Max loss|Liquidity drop|Crashed token|Hard profit" || echo "0")
HEARTBEATS=$(pm2 logs alpha-snipes-paper --lines 50000 --nostream | grep -E "$YESTERDAY|$(date -u +%Y-%m-%d)" | grep -cE "Heartbeat|Health check" || echo "0")
ERRORS=$(pm2 logs alpha-snipes-paper --lines 50000 --nostream | grep -E "$YESTERDAY|$(date -u +%Y-%m-%d)" | grep -cE "failed|error|Error" || echo "0")

TOTAL=$((ALPHA_TOUCHED + BOUGHT + SKIPPED + MILESTONES + EXITS + HEARTBEATS + ERRORS))

